		);

		register_block_pattern(
			'ainoblocks/freestyle-three',
				array(
					'title'      => __('Freestyle Three', 'ainoblocks'),
					'content'    => "<!-- wp:group {\"align\":\"full\",\"style\":{\"color\":{\"background\":\"#ffff4f\"}},\"paddingTop\":8,\"paddingBottom\":8} -->\n<div class=\"wp-block-group alignfull has-background pt__8 pb__8\" style=\"background-color:#ffff4f\"><div class=\"wp-block-group__inner-container\"><!-- wp:ainoblocks/grid-container -->\n<div class=\"wp-block-ainoblocks-grid-container alignwide\"><div class=\"wp-block-ainoblocks-grid-container__inner\"><!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":3,\"gridColumnEndDesktop\":11,\"gridColumnStartTablet\":1,\"gridColumnEndTablet\":13,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":13,\"justifyItem\":\"stretch\"} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__3 col_end_d__11 col_start_t__1 col_end_t__13 col_start_m__1 col_end_m__13 align-self__start justify-self__stretch no-stacking\"><!-- wp:paragraph {\"align\":\"center\",\"paddingBottom\":5} -->\n<p class=\"has-text-align-center pb__5\">START SMALL AND START NOW — YOU HAVE NOTHING TO LOOSE</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":1,\"gridColumnEndDesktop\":13,\"gridColumnStartTablet\":1,\"gridColumnEndTablet\":13,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":13,\"justifyItem\":\"center\"} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__1 col_end_d__13 col_start_t__1 col_end_t__13 col_start_m__1 col_end_m__13 align-self__start justify-self__center no-stacking\"><!-- wp:heading {\"textAlign\":\"center\",\"fontSize\":\"xxl\",\"style\":{\"typography\":{\"lineHeight\":\"0.9\"}},\"paddingBottom\":13} -->\n<h2 class=\"has-text-align-center has-xxl-font-size pb__13\" style=\"line-height:0.9\">ONE WHO CHASES AFTER TWO RED BOARS WON’T CATCH EVEN ONE.</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":5,\"gridColumnEndDesktop\":9,\"gridColumnStartTablet\":5,\"gridColumnE