		register_block_pattern(
			'ainoblocks/freestyle-four',
				array(
					'title'      => __('Freestyle Four', 'ainoblocks'),
					'content'    => "<!-- wp:group {\"align\":\"full\",\"paddingTop\":13,\"paddingBottom\":13} -->\n<div class=\"wp-block-group alignfull pt__13 pb__13\"><div class=\"wp-block-group__inner-container\"><!-- wp:ainoblocks/grid-container -->\n<div class=\"wp-block-ainoblocks-grid-container alignwide\"><div class=\"wp-block-ainoblocks-grid-container__inner\"><!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":1,\"gridColumnEndDesktop\":13,\"gridColumnStartTablet\":1,\"gridColumnEndTablet\":13,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":13} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__1 col_end_d__13 col_start_t__1 col_end_t__13 col_start_m__1 col_end_m__13 align-self__start justify-self__start no-stacking\"><!-- wp:heading {\"level\":1,\"fontSize\":\"xs\",\"paddingBottom\":14} -->\n<h1 class=\"has-xs-font-size pb__14\">/Design 2020</h1>\n<!-- /wp:heading --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":1,\"gridColumnEndDesktop\":8,\"gridColumnStartTablet\":1,\"gridColumnEndTablet\":7,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":13} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__1 col_end_d__8 col_start_t__1 col_end_t__7 col_start_m__1 col_end_m__13 align-self__start justify-self__start no-stacking\"><!-- wp:heading {\"fontSize\":\"l\",\"paddingBottom\":5} -->\n<h2 class=\"has-l-font-size pb__5\">MY CATS LOVES SMALL BOXES</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":8,\"gridColumnEndDesktop\":13,\"gridColumnStartTablet\":7,\"gridColumnEndTablet\":13,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":13} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__8 col_end_d__13 col_start_t__7 col_end_t__13 col_start_m__1 col_end_m__13 align-self__start justify-self__start no-stacking\"><!-- wp:paragraph {\"paddingTop\":0,\"paddingBottom\":8} -->\n<p class=\"pb__8\">Hokusai was the youngest artist to be named Actor of the Year by Nak-Mag Japan. He was also the first ever actor to top the Japan Courage Celebrity list by Forbes.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"paddingBottom\":13} -->\n<p class=\"pb__13\">The “Hokusia Effect\", coined by the Japan Business Research Institute, refers to his consistent valuable brand reputation ranking indicating his effectiveness as an endorser across different demographics.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:ainoblocks/grid-item --></div></div>\n<!-- /wp:ainoblocks/grid-container -->\n\n<!-- wp:ainoblocks/grid-container -->\n<div class=\"wp-block-ainoblocks-grid-container alignwide\"><div class=\"wp-block-ainoblocks-grid-container__inner\"><!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":1,\"gridColumnEndDesktop\":6,\"gridColumnStartTablet\":1,\"gridColumnEndTablet\":6,\"gridColumnStartMobile\":1,\"gridColumnEndMobile\":7,\"marginTopTablet\":0,\"marginTopMobile\":9} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__1 col_end_d__6 col_start_t__1 col_end_t__6 col_start_m__1 col_end_m__7 align-self__start justify-self__start no-stacking mt_t__0 mt_m__9\"><!-- wp:image {\"id\":7793,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://wpaino.s3.eu-central-1.amazonaws.com/1144x1430px-green.jpg\" alt=\"\" class=\"wp-image-7793\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":9,\"gridColumnEndDesktop\":13,\"gridColumnStartTablet\":9,\"gridColumnEndTablet\":13,\"gridColumnStartMobile\":8,\"gridColumnEndMobile\":13} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__9 col_end_d__13 col_start_t__9 col_end_t__13 col_start_m__8 col_end_m__13 align-self__start justify-self__start no-stacking\"><!-- wp:image {\"id\":7797,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://wpaino.s3.eu-central-1.amazonaws.com/896x1122-green.jpg\" alt=\"\" class=\"wp-image-7797\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:ainoblocks/grid-item -->\n\n<!-- wp:ainoblocks/grid-item {\"gridColumnStartDesktop\":5,\"gridColumnEndDesktop\":9,\"gridColumnStartTablet\":5,\"gridColumnEndTablet\":9,\"gridColumnStartMobile\":6,\"gridColumnEndMobile\":11,\"marginTopDesktop\":-18,\"marginTopTablet\":-16,\"marginTopMobile\":-9} -->\n<div class=\"wp-block-ainoblocks-grid-item col_start_d__5 col_end_d__9 col_start_t__5 col_end_t__9 col_start_m__6 col_end_m__11 align-self__start justify-self__start no-stacking mt_d__-18 mt_t__-16 mt_m__-9\"><!-- wp:image {\"id\":7797,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://wpaino.s3.eu-central-1.amazonaws.com/896x1122-green.jpg\" alt=\"\" class=\"wp-image-7797\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:ainoblocks/grid-item --></div></div>\n<!-- /wp:ainoblocks/grid-container --></div></div>\n<!-- /wp:group -->",
